mapper.update:
UPDATE board
SET read_cnt = NVL(read_cnt, 0) + 1
WHERE board_code = #{boardCode}
  AND reg_id != #{regId}

mapper.select:
SELECT A.*, B.total_cnt FROM ( SELECT TT1.rnum AS no, TT1.board_code, TT1.title, TT1.div, TT1.contents, TT1.id, TT1.nickname, TT1.read_cnt, TT1.mod_dt, TT1.mod_id FROM ( SELECT ROWNUM rnum, T1.* FROM ( SELECT * FROM board WHERE 1 = 1
AND div = #{div}
ORDER BY mod_dt DESC ) T1
WHERE ROWNUM <= (#{pageSize} * (#{pageNo} - 1) + #{pageSize})
) TT1
WHERE rnum >= (#{pageSize} * (#{pageNo} - 1) + 1)
) A CROSS JOIN ( SELECT COUNT(*) AS total_cnt FROM board WHERE 1 = 1
AND div = #{div}
) B

mapper.insert:
INSERT INTO board (
    board_code, title, div, contents, id, nickname,
    read_cnt, reg_dt, reg_id, mod_dt, mod_id
) VALUES (
    #{boardCode}, #{title}, #{div}, #{contents}, #{id}, #{nickname},
    0, SYSDATE, #{regId}, SYSDATE, #{regId}
)
