mapper.update:
UPDATE event
SET read_cnt = NVL(read_cnt, 0) + 1
WHERE event_code = #{ecode}

  AND reg_id != #{regId}

mapper.select:
SELECT A.*, B.total_cnt FROM ( SELECT TT1.rnum AS no, TT1.event_code AS ecode, TT1.email, TT1.title, TT1.div, TT1.contents, TT1.nickname, TT1.read_cnt AS readCnt, TT1.reg_dt AS regDt, TT1.reg_id AS regId, TT1.mod_dt AS modDt, TT1.mod_id AS modId FROM ( SELECT ROWNUM rnum, T1.* FROM ( SELECT * FROM event WHERE 1 = 1
ORDER BY mod_dt DESC ) T1
WHERE ROWNUM <= (#{pageSize} * (#{pageNo} - 1) + #{pageSize})
) TT1
WHERE rnum >= (#{pageSize} * (#{pageNo} - 1) + 1)
) A CROSS JOIN ( SELECT COUNT(*) AS total_cnt FROM event WHERE 1 = 1
) B

mapper.insert:

SELECT EVENT_SEQ.NEXTVAL FROM dual

INSERT INTO event (
  event_code, email, title, div, contents, nickname,
  read_cnt, reg_dt, reg_id, mod_dt, mod_id
) VALUES (
  #{ecode}, #{email}, #{title}, #{div}, #{contents}, #{nickname},
  0, SYSDATE, #{regId}, SYSDATE, #{modId}
)
