mapper.insert:
INSERT INTO MEMBER (
            ID, PWD, NICKNAME, NAME, PHONE, EMAIL,
            REG_DT, REG_ID, ADMIN_ROLE
        ) VALUES (
            #{id}, #{pwd}, #{nickname}, #{name}, #{phone}, #{email},
            SYSDATE, #{regId}, #{adminRole}
    )

mapper.select:
SELECT ID, PWD, NICKNAME, NAME, PHONE, EMAIL, REG_DT, REG_ID, MOD_DT, MOD_ID, ADMIN_ROLE
FROM MEMBER
WHERE ID = #{id}
